{
  "title": "Daily Life and Social Interactions: Greetings and Farewells",
  "level": "B1",
  "focus": "general",
  "estimatedTime": 60,
  "sections": [
    {
      "type": "warmup",
      "title": "Warm-up Activity",
      "content": "In this warm-up, students will explore the vocabulary words 'custom', 'gesture', 'respect', 'ritual', and 'etiquette' through a group brainstorming activity. They will discuss how greetings and farewells vary across cultures and why these practices are important.",
      "questions": 2,
      "targetVocabulary": 5,
      "procedure": "Begin by writing the target vocabulary words on the board: custom, gesture, respect, ritual, etiquette. Ask students to share examples of greetings or farewells they know from their own culture or others. For instance, ask them to describe a specific gesture or ritual they find interesting. Encourage them to use all five vocabulary words in their explanations. For example, 'A handshake is a common gesture that shows respect.' Afterward, summarize key points and clarify any misunderstandings about the vocabulary.",
      "teacherNotes": "Ensure students understand the meanings of the vocabulary words before moving on. Provide examples if needed and encourage participation by asking follow-up questions."
    },
    {
      "type": "reading",
      "title": "Reading Text",
      "introduction": "This reading explores how people around the world greet and say goodbye to one another. It highlights the cultural significance of these interactions and explains why they matter in daily life.",
      "paragraphs": 5,
      "teacherNotes": "Encourage students to pay attention to details about customs, gestures, and rituals mentioned in the text. Highlight the importance of understanding cultural differences."
    },
    {
      "type": "vocabulary",
      "title": "Key Vocabulary",
      "words": 5
    },
    {
      "type": "comprehension",
      "title": "Reading Comprehension",
      "questions": 5
    },
    {
      "type": "sentenceFrames",
      "title": "Sentence Practice",
      "frames": 2
    },
    {
      "type": "discussion",
      "title": "Discussion Questions",
      "introduction": "Greetings and farewells are more than just polite exchanges—they reflect deeper values like respect and connection. Let’s explore how these practices shape our relationships and communities.",
      "questions": 5
    },
    {
      "type": "quiz",
      "title": "Knowledge Check",
      "questions": 5
    }
  ]
}
