      "questions": [
        "Do you think superstitions are harmful or helpful?",
        "How do superstitions influence people's decisions?",
        "Are there any superstitions you would like to share from your own culture?",
        "How do legends and myths shape cultural identity?",
        "What makes a good story or legend compelling?"
      ]
    },
    {
      "type": "quiz",
      "title": "Quiz: Test Your Knowledge",
      "questions": [
        {
          "question": "What is the main characteristic of a leprechaun?",
          "options": [
            "Kindness",
            "Generosity",
            "Mischief",
            "Wisdom"
          ],
          "answer": "Mischief"
        },
        {
          "question": "What do leprechauns supposedly guard?",
          "options": [
            "Magical artifacts",
            "Ancient secrets",
            "Pots of gold",
            "Rare jewels"
          ],
          "answer": "Pots of gold"
        }
      ]
    }
  ]
}
